import React from "react";

const teamMembers = [
  { name: "Alice", role: "Frontend Developer", img: "https://randomuser.me/api/portraits/women/1.jpg" },
  { name: "Bob", role: "Backend Developer", img: "https://randomuser.me/api/portraits/men/2.jpg" },
  { name: "Charlie", role: "UI/UX Designer", img: "https://randomuser.me/api/portraits/men/3.jpg" },
];

const Team = () => (
  <section id="team" className="p-10 bg-white text-center">
    <h2 className="text-3xl font-bold mb-6">Our Team</h2>
    <div className="flex flex-wrap justify-center gap-8">
      {teamMembers.map((member, i) => (
        <div key={i} className="bg-gray-100 p-4 rounded-lg shadow-md w-60">
          <img src={member.img} alt={member.name} className="w-24 h-24 mx-auto rounded-full mb-4" />
          <h3 className="text-xl font-semibold">{member.name}</h3>
          <p className="text-sm text-gray-600">{member.role}</p>
        </div>
      ))}
    </div>
  </section>
);

export default Team;
